-- 
-- Заполняем данными таблицу "Книги".
-- 
-- 
-- Обновить инфорамацмю в таблице "Книги", после вставки данных.
-- 
-- Обновляем дату создания записи
UPDATE books SET created_at=DATE_FORMAT(created_at,'2020-%m-%d %T');
-- 
-- Обновляем серию
UPDATE books 
  SET series_id = (SELECT series_id FROM series WHERE series.publishing_house_id = books.publishing_house_id  ORDER BY RAND() LIMIT 1);
-- 
-- Обновляем язык
UPDATE books 
  SET `language` = (SELECT language FROM (SELECT 'en' AS language UNION SELECT 'ru' UNION SELECT 'ge') tbl ORDER BY RAND() LIMIT 1);
-- 
-- Обновляем Тип облжки
UPDATE books 
  SET cover_type = (SELECT cover_type FROM (SELECT 'Твердая глянцевая' AS cover_type UNION SELECT 'Суперобложка'
  						UNION SELECT 'Мягкая бумажная' UNION SELECT 'Твердая бумажная') tbl ORDER BY RAND() LIMIT 1);

SELECT * FROM books;

